import React, {useState} from 'react';


const sidebarStyle = {
    width: '270px'
};

const imageStyle = {
    width: '40px',
    height: '40px'
};


const Sidebar = ({onButtonClick}) => {

    const [activeButton, setActiveButton] = useState(1);

    const ButtonClick = (buttonNumber) =>{
        setActiveButton(buttonNumber);
    };

  return (
    <>
        <div class="sidebar pe-4 pb-3 d-none d-md-block h-100" style={sidebarStyle}>
            <nav class="navbar bg-light navbar-light d-flex align-items-start h-100 p-0" style={sidebarStyle}>
                <div class="my-auto ms-2 mt-0">
                <a href="index.html" class="navbar-brand mx-4 mb-3">
                    <h3 class="text-primary"><i class="fa fa-hashtag me-2"></i>KingMaker</h3>
                </a>
                <div class="d-flex align-items-center ms-4 mb-4">
                    <div class="position-relative">
                        <img class="rounded-circle" src="img/user.jpg" alt="" style={imageStyle} />
                        <div class="bg-success rounded-circle border border-2 border-white position-absolute end-0 bottom-0 p-1"></div>
                    </div>
                    <div class="ms-3">
                        <h6 class="mb-0 userName">UserName</h6>
                        <span class="role">Role</span>
                    </div>
                </div>
                <div class="navbar-nav w-100">
                    <button class={`nav-link ${activeButton === 1 ? 'active' : ''}`} onClick={() => onButtonClick(1)}><i class="fa fa-tachometer-alt me-2"></i>Dashboard</button>
                    <button class={`nav-link ${activeButton === 2 ? 'active' : ''}`} onClick={() => onButtonClick(2)}><i class="fa fa-th me-2"></i>연습 시작</button>
                    <button class={`nav-link ${activeButton === 3 ? 'active' : ''}`} onClick={() => onButtonClick(3)}><i class="fa fa-th me-2"></i>연습 중</button>
                    <button class={`nav-link ${activeButton === 4 ? 'active' : ''}`} onClick={() => onButtonClick(4)}><i class="fa fa-th me-2"></i>연습 끝 (분석) </button>
                    <button class={`nav-link ${activeButton === 5 ? 'active' : ''}`} onClick={() => onButtonClick(5)}><i class="fa fa-th me-2"></i>발표 예상 질문</button>
                </div>
                </div>
            </nav>
        </div>
    </>
  );
};

export default Sidebar;
