import React from "react";
import {Container, Row, Col} from 'react-bootstrap';
import './scroll.css';

const RecentPractice =()=>{

    const getContainerStyle =()=>({
        // overflowX: 'auto',
        // whiteSpace: 'nowrap'
    });

    const getItemStyle=()=>({
        // display: 'inline-block',
    });

    const imgs = [
        'img/ppt1.jpg',
        'img/ppt2.png',
        'img/ppt3.png',
        'img/ppt4.jpg',
    ];

    return(
        <>
        <div class=" pt-4 px-4 m-0"> 
            <div class="bg-light rounded p-4">
                <Container>
                    <Row class="image-container">
                        {imgs.map((path, index)=>(
                            <Col key={index}>
                                <img src={path} alt={`Image ${index + 1}`} class="img-fluid" />
                            </Col>
                        ))}
                    </Row>
                </Container>
                {/* <h6 class="mb-0">최근 연습</h6>
                <div class="d-flex align-items-center overflow-auto" style={getContainerStyle()}>
                    {imgs.map((path, index) => (
                        <div key={index} class="" style={getItemStyle()}>
                            <button class='btn btn-link'>
                                <img src={path} class="d-block" alt={`이미지 ${index + 1}`} />
                            </button>
                        </div>
                    ))}
                </div> */}
            </div>
        </div>
        </>
    );
};

export default RecentPractice;
