import logo from './logo.svg';
import './App.css';
import Sidebar from './component/sidebar';
import Navbar from './component/navbar';
import After_practice from './pages/after_practice';
import Before_practice from './pages/before_practice';
import Dashboard from './pages/dashboard';
import Expected_question from './pages/expected_question';
import Middle_practice from './pages/middle_practice';

import React, {useState} from 'react';


function App() {

  const [currentPage, setPage] = useState(1);

  const changePage = (pageNumber) =>{
    setPage(pageNumber);
  };

  const renderPage = () =>{
    switch(currentPage) {
      case 1:
        return <Dashboard />;
      case 2:
        return <Before_practice />;
      case 3:
        return <Middle_practice />;
      case 4:
        return <After_practice />;
      case 5:
        return <Expected_question/>;
      default:
        return <Dashboard />;
    }
  };

  return (
    <div className="App h-100">

      <div className="container-xxl position-relative bg-white d-flex p-0 m-0 h-100">
        <Sidebar handlePage={changePage}/>

        <div class="content ml-sm-auto">
          <Navbar/>
          <h3>{currentPage}</h3>

          {renderPage()}
        </div>
      </div>

    </div>
  );
}

export default App;
