import React from "react";

const RecentPractice =()=>{

    const getContainerStyle =()=>({
        whiteSpace: 'nowrap',
        width: '100%'
    });

    const getItemStyle=()=>({
        display: 'inline-block',
        marginRight: '5px'
    });

    const imgs = [
        'img/ppt1.jpg',
        'img/ppt2.png',
        'img/ppt3.png',
        'img/ppt4.jpg',
    ];

    return(
        <>
        <div class="container-fluid pt-4 px-4"> 
            <div class="h-100 bg-light rounded p-4">
                
                <h6 class="mb-0">최근 연습</h6>
                <div class="scroll-container" style={getContainerStyle()}>
                    {imgs.map((path, index) => (
                        <div key={index} class="scroll-time-recent" style={getItemStyle()}>
                            <button>
                                <img src={path} class="d-block mx-auto" alt={`이미지 ${index + 1}`} />
                            </button>
                        </div>
                    ))}
                </div>
            </div>
        </div>
        </>
    );
};

export default RecentPractice;
